// Utility functions
const parseConcentration = (drugName) => {
  const match = drugName.match(/(\d+(?:\.\d+)?)\s*(mg|mcg|g|IU)\s*\/\s*(\d+(?:\.\d+)?)\s*(mL|L)/i);
  if (match) {
    return { amount: parseFloat(match[1]), unit: match[2].toLowerCase(), per: parseFloat(match[3]), perUnit: match[4].toLowerCase() };
  }
  const simple = drugName.match(/(\d+(?:\.\d+)?)\s*(mg|mcg|g|IU)\s+(?:Tablet|Capsule|Chewable|Film)/i);
  if (simple) {
    return { amount: parseFloat(simple[1]), unit: simple[2].toLowerCase(), per: 1, perUnit: 'unit' };
  }
  return null;
};

const parseVolume = (drugName) => {
  const match = drugName.match(/(\d+(?:\.\d+)?)\s*mL\s+(?:Syrup|Suspension|Oral|Solution|Drops|Bottle|Elixir)/i);
  return match ? parseFloat(match[1]) : null;
};

const isLiquid = (drugName) => {
  const liquidForms = ['syrup', 'suspension', 'oral drops', 'oral solution', 'elixir', 'drops bottle'];
  return liquidForms.some(f => drugName.toLowerCase().includes(f));
};

const isTabletOrCapsule = (drugName) => /tablet|capsule|chewable/i.test(drugName);

const formatNumber = (num, decimals = 2) => {
  if (num === null || num === undefined || isNaN(num)) return '';
  return num.toLocaleString('en-PH', { minimumFractionDigits: 0, maximumFractionDigits: decimals });
};

const formatCurrency = (num) => {
  if (num === null || num === undefined || isNaN(num)) return '';
  return '' + num.toLocaleString('en-PH', { minimumFractionDigits: 2, maximumFractionDigits: 2 });
};

